import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import Home from "./pages/Home";
import Web19202 from "./pages/Web19202";
import Web192015 from "./pages/Web192015";
import PlayerLobby from "./pages/PlayerLobby";
import HostSetSelect from "./pages/HostSetSelect";
import Web19209 from "./pages/Web19209";
import PlayerMemorizing from "./pages/PlayerMemorizing";
import HostLobby from "./pages/HostLobby";
import Web19204 from "./pages/Web19204";
import Login from "./pages/Login";

import Web19201 from "./pages/Web19201";
import { useEffect } from "react";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action]);

  //ADD Meta Tags

  return (
    <Routes>
      <Route path="/" element={<Login />} />

      <Route path="/web-1920-2" element={<Web19202 />} />

      <Route path="/web-1920-15" element={<Web192015 />} />

      <Route path="/player/lobby" element={<PlayerLobby />} />

      <Route path="/host/setselect" element={<HostSetSelect />} />

      <Route path="/web-1920-9" element={<Web19209 />} />

      <Route path="/player/memorizing" element={<PlayerMemorizing />} />

      <Route path="/web-1920-5" element={<HostLobby />} />

      <Route path="/web-1920-4" element={<Web19204 />} />

      <Route path="/home" element={<Home />} />

      <Route path="/web-1920-1" element={<Web19201 />} />
    </Routes>
  );
}
export default App;
